(29267,	20152,	20116,	20120,	NULL,	'2018-05-17 14:04:29.558000',	29264,	0,	29265,	'1.0',	'<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,zh_CN,es_ES,ja_JP,nl_NL,hu_HU,pt_BR,de_DE,iw_IL,fi_FI,ca_ES,fr_FR,\" default-locale=\"en_US\"><Name language-id=\"zh_CN\">Carousel</Name><Name language-id=\"es_ES\">Carrusel</Name><Name language-id=\"ja_JP\">カルーセル</Name><Name language-id=\"nl_NL\">Carrousel</Name><Name language-id=\"hu_HU\">Körhinta</Name><Name language-id=\"pt_BR\">Carrossel</Name><Name language-id=\"de_DE\">Karussel</Name><Name language-id=\"iw_IL\">קרוסלה</Name><Name language-id=\"fi_FI\">Karuselli</Name><Name language-id=\"ca_ES\">Carrussel</Name><Name language-id=\"fr_FR\">Carrousel</Name><Name language-id=\"en_US\">Carousel</Name></root>',	'<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,zh_CN,es_ES,ja_JP,nl_NL,hu_HU,pt_BR,de_DE,iw_IL,fi_FI,ca_ES,fr_FR,\" default-locale=\"en_US\"><Description language-id=\"zh_CN\">把图片显示在转盘中。</Description><Description language-id=\"es_ES\">Muestra imágenes en un carrusel.</Description><Description language-id=\"ja_JP\">カルーセル内にイメージを表示</Description><Description language-id=\"nl_NL\">Geeft afbeeldingen weer in een carrousel.</Description><Description language-id=\"hu_HU\">Megjeleníti a képeket körhintaszerűen.</Description><Description language-id=\"pt_BR\">Exibe imagens num carrossel.</Description><Description language-id=\"de_DE\">Darstellung von Bildern in einem Karussell.</Description><Description language-id=\"iw_IL\">מציג את התמונות בקרוסלה.</Description><Description language-id=\"fi_FI\">Näyttää kuvat karusellissa.</Description><Description language-id=\"ca_ES\">Mostra imatges en format carrusel.</Description><Description language-id=\"fr_FR\">Affiche les images dans un carrousel.</Description><Description language-id=\"en_US\">Displays images in a carousel.</Description></root>',	'ftl',	'<#if entries?has_content>\n	<style>\n		#<@portlet.namespace />carousel .carousel-item {\n			background-color: #000;\n			height: 250px;\n			overflow: hidden;\n			text-align: center;\n			width: 700px;\n		}\n\n		#<@portlet.namespace />carousel .carousel-item img {\n			max-height: 250px;\n			max-width: 700px;\n		}\n	</style>\n\n	<div id=\"<@portlet.namespace />carousel\">\n		<#assign imageMimeTypes = propsUtil.getArray(\"dl.file.entry.preview.image.mime.types\") />\n\n		<#list entries as entry>\n			<#if imageMimeTypes?seq_contains(entry.getMimeType())>\n				<div class=\"carousel-item image-viewer-base-image\">\n					<img src=\"${dlUtil.getPreviewURL(entry, entry.getFileVersion(), themeDisplay, \"\")}\" />\n				</div>\n			</#if>\n		</#list>\n	</div>\n\n	<@liferay_aui.script use=\"aui-carousel\">\n		var carousel = new A.Carousel(\n			{\n				after: {\n					responsive: function(event) {\n						event.stopImmediatePropagation();\n\n						var boundingBox = event.currentTarget.get(\'boundingBox\');\n\n						boundingBox.all(\'.image-viewer-base-image-list, .image-viewer-base-image\').setStyles(\n							{\n								height: \'auto\',\n								maxHeight: event.height,\n								maxWidth: event.width,\n								width: \'auto\'\n							}\n						);\n					}\n				},\n\n				contentBox: \'#<@portlet.namespace />carousel\',\n				height: 250,\n				intervalTime: 2,\n				width: 700\n			}\n		).render();\n	</@liferay_aui.script>\n</#if>',	0,	20120,	NULL,	'2018-05-17 14:04:29.558000'),
