(29280,	20152,	20116,	20120,	NULL,	'2018-05-17 14:04:29.808000',	29268,	0,	29278,	'1.0',	'<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,zh_CN,es_ES,ja_JP,nl_NL,hu_HU,pt_BR,de_DE,iw_IL,fi_FI,ca_ES,fr_FR,\" default-locale=\"en_US\"><Name language-id=\"zh_CN\">选择框</Name><Name language-id=\"es_ES\">Caja de selección</Name><Name language-id=\"ja_JP\">セレクトボックス</Name><Name language-id=\"nl_NL\">Keuze veld</Name><Name language-id=\"hu_HU\">Választó doboz</Name><Name language-id=\"pt_BR\">Caixa de Seleção</Name><Name language-id=\"de_DE\">Auswahlliste</Name><Name language-id=\"iw_IL\">תיבת בחירה</Name><Name language-id=\"fi_FI\">Valintalaatikko</Name><Name language-id=\"ca_ES\">Caixa de selecció</Name><Name language-id=\"fr_FR\">Sélectionner boite</Name><Name language-id=\"en_US\">Select Box</Name></root>',	'<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,zh_CN,es_ES,ja_JP,nl_NL,hu_HU,pt_BR,de_DE,iw_IL,fi_FI,ca_ES,fr_FR,\" default-locale=\"en_US\"><Description language-id=\"zh_CN\">显示所选语言的选择框</Description><Description language-id=\"es_ES\">Muestra una caja de selección con los idiomas seleccionados.</Description><Description language-id=\"ja_JP\">選択された言語でアイコンメニューを表示する</Description><Description language-id=\"nl_NL\">Toen keuze veld met de geselecteerde talen.</Description><Description language-id=\"hu_HU\">Megjeleníti az ikon menüt a választott nyelveken.</Description><Description language-id=\"pt_BR\">Exibe uma caixa de seleção com os idiomas selecionados.</Description><Description language-id=\"de_DE\">Zeigt eine Auswahllist mit den ausgewählten Sprachen an</Description><Description language-id=\"iw_IL\">מציג תיבת בחירה עם השפות הנבחרות.</Description><Description language-id=\"fi_FI\">Näyttää valintalaatikon valituilla kielillä.</Description><Description language-id=\"ca_ES\">Mostra un quadre de selecció amb els idiomes seleccionats</Description><Description language-id=\"fr_FR\">Affiche une boite sélectionnée avec les langues sélectionnées.</Description><Description language-id=\"en_US\">Displays a select box with the selected languages</Description></root>',	'ftl',	'<#if entries?has_content>\n	<#assign languageId = localeUtil.toLanguageId(locale) />\n\n	<style>\n		.taglib-language-option {\n			background: none no-repeat 5px center;\n			padding-left: 25px;\n		}\n\n		<#list entries as entry>\n			.taglib-language-option-${entry.getW3cLanguageId()} {\n				background-image: url(${themeDisplay.getPathThemeImages()}/language/${entry.getLanguageId()}.png);\n			}\n		</#list>\n	</style>\n\n	<@liferay_aui[\"form\"]\n		action=formAction\n		method=\"post\"\n		name=\'${namespace + formName}\'\n		useNamespace=false\n	>\n		<@liferay_aui[\"select\"]\n			changesContext=true\n			id=\'${namespace + formName}\'\n			label=\"\"\n			name=\'${name}\'\n			onChange=\'${namespace + \"changeLanguage();\"}\'\n			title=\"language\"\n		>\n			<#list entries as entry>\n				<@liferay_aui[\"option\"]\n					cssClass=\"taglib-language-option taglib-language-option-${entry.getW3cLanguageId()}\"\n					disabled=entry.isDisabled()\n					label=entry.getLongDisplayName()\n					lang=entry.getW3cLanguageId()\n					selected=entry.isSelected()\n					value=entry.getLanguageId()\n				/>\n			</#list>\n		</@>\n	</@>\n\n	<@liferay_aui[\"script\"]>\n		function ${namespace}changeLanguage() {\n			var languageId = AUI.$(document.${namespace + formName}.${name}).val();\n\n			submitForm(document.${namespace + formName});\n		}\n	</@>\n</#if>',	0,	20120,	NULL,	'2018-05-17 14:04:29.808000'),
